import React, { useState } from 'react';
import './App.css';
// import YourPromises from './components/YourPromises'
import AddButton from './components/AddButton'
import PromiseContent from './components/promise-components/PromiseContent';
import PromiseWhen from './components/promise-components/PromiseWhen';
import PromiseWhere from './components/promise-components/PromiseWhere';
import PhoneNumber from './components/promise-components/PhoneNumber';

function App() {
  const [promise, setPromise] = useState('')
  const [date, setDate] = useState('')
  const [time, setTime] = useState('')
  const [place, setPlace] = useState('')
  const [phone, setPhone] = useState('')
  const [submit, setSubmit] = useState(false)
  const [isValid, setIsValid] = useState(false)

  function formValidate () {
    return true
  }

  function handleSubmit (e) {
    e.preventDefault()
    if (formValidate === true) {
      return setSubmit(true)
    } else if (formValidate === false) {
      return setIsValid(false)
    }
    console.log("Button clicked")

  }

  return (
  <>
  <section className="hero is-primary">
    <div className="hero-body">
      <div className="container">
        <h1 className="title">
          Promises
        </h1>
        <h2 className="subtitle">
          We will send you a text message so that you don't forget your promises &#128526;
        </h2>
      </div>
    </div>
  </section>
  <form onSubmit={handleSubmit}>
  <section className="main-body">
    <div className="inputs">
    <br></br><br></br>
    <div className="tile is-ancestor">
      <div className="tile is-parent">
        <article className="tile is-child box">
          <PromiseContent promise={promise} setPromise={setPromise}/>
        </article>
      </div>
      <div className="tile is-parent">
        <article className="tile is-child box">
          <PromiseWhen date={date} setDate={setDate} time={time} setTime={setTime}/>
        </article>
      </div>
    <div className="tile is-parent">
      <article className="tile is-child box">
          <PromiseWhere place={place} setPlace={setPlace} />
      </article>
    </div>
    <div className="tile is-parent">
        <article className="tile is-child box">
          <PhoneNumber phone={phone} setPhone={setPhone} />
        </article>
      </div>
  </div>
    </div>
    <br></br><br></br>
    <div className="add-button">
    <AddButton />
    </div>
  </section>
  </form>
  </>
  )}

export default App;
