import React from 'react'

function PromiseWhen (props) {

    function handleDate (e) {
        // console.log(e)
        setDate(e.currentTarget.value)
        console.log(e.currentTarget.value)
    }

    return (
          <>
          <p className="title">Select date & time for your notification</p>
          <div className="field">
            <div className="control">
                <input className="input is-info" setDate={props.setDate} value={props.date} onChange={handleDate} type="date" placeholder="Info input"></input>
            </div>
                <br></br>
            <div className="control">
                <input className="input is-info" type="time" placeholder="Info input"></input>
            </div>
         </div>
          </>
    )
}



export default PromiseWhen;
