import React from 'react'

function PhoneNumber (props) {

    function isValid (e) {
        const phoneno = /^\d{10}$/;
        if (e.target.value.match(phoneno)) {
            return true
        } else {
            alert("Please enter 10 digits of your phone number")
            return false
        }
    }

    function handlePhone (e) {
        if (isValid === true) {
            return props.setPhone(e.currentTarget.value)
        } else {
            return false
        }
        console.log(e.currentTarget.value)
    }

    return (
          <>
          <p className="title">Enter your Phone number to receive a notification</p>
          <div className="field">
            <div className="control">
                <input className="input is-danger" value={props.phone} onChange={handlePhone} type="tel" placeholder="Enter your phone number here"></input>
            </div>
          </div>
          </>
    )
}


export default PhoneNumber;
