import React, { useState } from 'react'

function PhoneNumber (props) {
    const [isValid, setIsValid] = useState(false)

    function formValid (input) {
        console.log(input)
        if (input.value === '') {
            return false
        } else if (input.value === NaN) {
            return false
        } else if ((input.value).length < 10) {
            return false
        } else {
            return true
        }
    }

    function handlePhone (e) {
        if (formValid === true) {
        return (props.setPhone(e.currentTarget.value)
        )} else if (formValid === false) {
            alert("Enter 10 digits of phone number")
            return (
                setIsValid(false)
            )
        }
    }

    return (
          <>
          <p className="title">Enter your Phone number to receive a notification</p>
          <div className="field">
            <div className="control">
                <input className="input is-danger" value={props.phone} onChange={handlePhone} type="tel" placeholder="Enter your phone number here"></input>
            </div>
          </div>
          </>
    )
}


export default PhoneNumber;
